import "./App.css";
import BadWordsFilter from "bad-words";
import axios from "axios";
const Filter = require("bad-words");

function App() {
  return (
    <div className="App">
      <div id="background"></div>
      <img src={require("./images/background.png")} alt="" id="bg-top" />
      <img src={require("./images/background.png")} alt="" id="bg" />
      <div className="container">
        <div className="row t1 d-flex flex-column p-5">
          <div className="col-10 offset-1 text-center d-flex justify-content-center align-items-center">
            <h1 className="display-4 m-0 " id="welcome">
              Welcome to AI Jokes
            </h1>
            <img
              src={require("./images/logo.png")}
              id="logo"
              className="mx-4"
            />
          </div>
          <div className="col-10 mt-4 offset-1 d-flex d-flex justify-content-center align-items-center">
            <p className="d-inline m-0">Tell me a joke about...</p>
            <input type="text" className="mx-1" id="subject"></input>
            <button onClick={handleSubmit}>Generate</button>
          </div>
        </div>
      </div>
    </div>
  );
}
document.onkeydown = function (event) {
  if (event.key == "Enter") {
    handleSubmit();
  }
};
function handleResponse(response) {
  alert(response.data.answer);
}
function handleSubmit() {
  let userinput = trim(document.getElementById("subject").value);
  /*User input goes through TWO profanity filters.

  First filter is from bad words npm package: https://www.npmjs.com/package/bad-words */
  const filter = new Filter();
  if (filter.isProfane(userinput)) {
    alert("Please be respectful to the AI.");
    // https://www.youtube.com/watch?v=25f2IgIrkD4
  } else {
    //The bad words npm package doesn't cover everything, so I made this regular expression to cover more.
    var re =
      /((fuck*)|(shit*)|(bitch*)|(porn*)|(assh*)|(ballsack*)|(whore*)|(hentai*)|(cunt*))/;
    //teehee lol ^
    let profanityFilter2 = re.test(userinput);
    //Test the user input, if true alert the same thing and if not, send to SheCodes API
    if (profanityFilter2 === true) {
      alert("Please be respectful to the AI.");
    } else if (
      userinput === null ||
      userinput === undefined ||
      userinput === ""
    ) {
      alert("Please type a joke subject before submitting");
    } else {
      let apiKey = "f7o330d7cc44511d503ab6b4tdbb899b";
      let prompt = `Tell me a joke about ${userinput}`;
      let context = "PLease only tell the joke, nothing more.";

      let apiURL = `https://api.shecodes.io/ai/v1/generate?prompt=${prompt}&context=${context}&key=${apiKey}`;
      axios.get(apiURL).then(handleResponse);
    }
  }
}
export default App;
